package com.oracle.bmc.auth.internal;

import java.security.interfaces.RSAPublicKey;
import java.text.ParseException;
import java.util.Date;
import java.util.Optional;

import org.junit.Test;

import static org.junit.Assert.assertEquals;
import static org.junit.Assert.assertNull;
import static org.junit.Assert.assertTrue;

public class JwtClaimsSetTest {
    static final String fakeToken =
            "eyJraWQiOiJhc3dfb2MxX2RlMWIxZmM3IiwiYWxnIjoiUlMyNTYifQ"
                    + "."
                    + ""
                    + "."
                    + "KG8tVWD6Nl8SiuG9zDXnZjGZVzbaGoyBABZwBQZLyaxu0lB5xXdm_RYtvyR0yygULwLvuIPzbkwTSJcJE5HjReQUIdqTP_P4E4fGbVdwrU6dYNsTOMf2jZZVQtUP7iWsJKrvOzs1ZwI1nmoZ5uRUSNq6sUbznHWk8qvdPycEuR6n5aElrXp0cHgfg9IxAU1CnnVjklIJHZslCIL-7GV2jWjn5ae456pAYFF0ihpEHpaMTnBdNkYjHq3rDnOXG3194E5_rQzwdLYhliID5uYhRr8bvfm2fvH9UMSA8ZLkSPg3Xc_z_2xrlawguLjw5Bw5-UojFYFfcJIiyXE6Qqtlqw";

    @Test(expected = NullPointerException.class)
    public void testNullToken() throws ParseException {
        JwtClaimsSet jwt = new JwtClaimsSet(null);
    }

    @Test(expected = IllegalArgumentException.class)
    public void testBlankToken() throws ParseException {
        JwtClaimsSet jwt = new JwtClaimsSet("");
    }

    @Test
    public void testToken() throws ParseException {
        JwtClaimsSet jwt = new JwtClaimsSet(fakeToken);

        Date expirationTime = jwt.getExpirationTime();
        assertEquals(1629970866000L, expirationTime.getTime());

        assertEquals(1629970866000L, ((Date) jwt.getClaim("exp")).getTime());
        assertEquals("x509", jwt.getStringClaim("ttype"));
        assertNull(jwt.getStringClaim("foo"));

        assertEquals("RS256", jwt.getHeader().get("alg"));
        assertEquals(
                "6C:33:C0:7A:01:17:3D:3A:D9:50:F3:3D:E1:7B:EC:A9:83:11:9C:57",
                jwt.getPayload().get("fprint"));
        assertEquals("instance", jwt.getPayload().get("ptype"));
        assertEquals("oci", jwt.getPayload().get("aud"));
        assertEquals(
                "V1,ocid1.dynamicgroup.oc1..aaaaaaaa,3gmypvxxk2jx2znxh27r6wa5zhafblnvytbifljtj7d6nhsgd4ga,h5ew7eqj3qbgkiyy3mnthnxgto7hqbykwogo3bb4gufsgmkwuqfq",
                jwt.getPayload().get("opc-tag"));
        assertEquals("x509", jwt.getPayload().get("ttype"));
        assertEquals(
                "ocid1.instance.oc1.iad.anuwcljt46clddyc77t7pjsxqquqwhpndm7bruayfwe63fdrdeuo4ceok4pa",
                jwt.getPayload().get("opc-instance"));
        assertEquals(
                "ocid1.tenancy.oc1..aaaaaaaak7qsj6ltfwek5cxl6xptznanr2kt7fxrbho7di3vchaf6m5apxeq",
                jwt.getPayload().get("opc-tenant"));
        assertEquals(new Date(1629970866L * 1000L), jwt.getPayload().get("exp"));
        assertEquals(new Date(1629969666L * 1000L), jwt.getPayload().get("iat"));
        // No key? assertEquals("1629969666", jwt.getPayload().get("key"));
        assertEquals("authService.oracle.com", jwt.getPayload().get("iss"));
        // No nbf? assertEquals(new Date(1629969666L*1000L), jwt.getPayload().get("nbf"));

        String jwk = jwt.getStringClaim("jwk");
        assertEquals(
                "{\"kid\":\"6C:33:C0:7A:01:17:3D:3A:D9:50:F3:3D:E1:7B:EC:A9:83:11:9C:57\",\"n\":\"\",\"e\":\"AQAB\",\"kty\":\"RSA\",\"alg\":\"RS256\",\"use\":\"sig\"}",
                jwk);

        // we may have to replace('-', '+').replace('_', '/') in the module (jwk.n)
        // see
        // https://stackoverflow.com/questions/28584080/base64-java-lang-illegalargumentexception-illegal-character
        Optional<RSAPublicKey> jwkRsa = AuthUtils.toPublicKeyFromJson(jwk);
        assertTrue(jwkRsa.isPresent());
    }
}
